Function Verifier {
param ([string] $path)
    Show-Pleasewait -path $script:curpath
    #$outflnm="$env:userprofile\desktop\test.csv"
    $hkcr='HKEY_CLASSES_ROOT'
    $hklm='HKEY_LOCAL_MACHINE'
    $strSubKey=''
    $strNewSource = ""
    $strNewRTMSource = ""
    $strComputer = "."
    $strSQLName = "*SQL*"
    $strDotNetName = ".NET"
    $strVStudioName = "Visual Studio"
    $strXML = "XML"
    $strOWC = "Microsoft Office 2003 Web Components"
    $strKeyPath = "Installer\Products"
    $strKeyPath2 = "SOFTWARE\Microsoft\Windows\CurrentVersion\Installer\UserData\S-1-5-18\Products"
    $strNValue00 = "ProductName"
    $strNValue01 = "PackageName"
    $strNValue02 = "LastUsedSource"
    $strNValue03 = "InstallSource"
    $strNValue04 = "LocalPackage"
    $strNValue05 = "DisplayVersion"
    $strNValue06 = "InstallDate"
    $strNValue07 = "UninstallString"
    $strNValue08 = "PackageCode"
    $strNValue09 = "MediaPackage"
    $strNValue10 = "InstallSource"
    $strNValue11 = "AllPatches"
    $strNValue12 = "NoRepair"
    $strNValue13 = "MoreInfoURL"
    $strNValue14 = "PackageName"
    $strNValue15 = "LastUsedSource"
    $strNValue16 = "Uninstallable"
    $strNValue17 = "DisplayName"
    $strNValue18 = "Installed"
    $strNewSource = ""
    $strNewRTMSource = ""
    $regKey = "Registry::HKEY_CLASSES_ROOT\Installer\Products";
    $rKeys = Get-ChildItem -Path $regKey -ea SilentlyContinue
    if (test-path $script:outputflnm) {remove-item "$script:outputflnm" -ea 0}
    foreach($strSubKey in $rKeys  ) {
        $strProduct01 = "$strSubKey"
        $strKeyPath02 = "$strSubKey\SourceList"
        $strKeyPath03 = "$strSubKey\SourceList\Media"
        $ckey=($strSubKey.name).replace('HKEY_CLASSES_ROOT\Installer\Products\','')
       # WRITE-HOST $strSubKey
       # WRITE-HOST $CKEY
        $strInstallSource = "SOFTWARE\Microsoft\Windows\CurrentVersion\Installer\UserData\S-1-5-18\Products\$cKey\InstallProperties\"
        $strInstallSource2 = "SOFTWARE\Microsoft\Windows\CurrentVersion\Installer\UserData\S-1-5-18\Products\$cKey\patches\"
        $strInstallSource3 = "SOFTWARE\Microsoft\Windows\CurrentVersion\Installer\UserData\S-1-5-18\Patches"
        $strInstallSource5 = "SOFTWARE\Classes\Installer\Patches\"
        $strInstallSource6 = "SOFTWARE\Microsoft\Windows\CurrentVersion\Uninstall\"
        $strInstallSource7 = "SOFTWARE\Microsoft\Microsoft SQL Server\"
        $strInstallSource8 = "SOFTWARE\Wow6432Node\Microsoft\Microsoft SQL Server\"
        $strRetValue00=Get-ItemProperty -Path ("Registry::$strProduct01") -Name "ProductName" -ea SilentlyContinue
        if (($strRetValue00.productname) -ilike $strSQLName)   {    
            $strRetValue01=Get-ItemProperty -Path ("Registry::$strKeyPath02") -Name "PackageName" -ea SilentlyContinue
            $strNValue08=Get-ItemProperty -Path ("Registry::$strProduct01") -Name "PackageCode" -ea SilentlyContinue
            $strRetValue02=Get-ItemProperty -Path ("Registry::$strKeyPath02") -Name "LastUsedSource" -ea SilentlyContinue
            if ($strRetValue02 ) {$strRetValNew02=$strRetValue02.LastUsedSource.substring(4) } else {$strRetValNew02 =""}
            $strRetValue09= Get-ItemProperty -Path ("Registry::$strKeyPath03") -Name $strNValue09 -ea SilentlyContinue
            $strRetValNew03= Get-ItemProperty -Path ("Registry::$hklm\$strInstallSource") -Name $strNValue03     -ea SilentlyContinue
            $strRetValNew04 = Get-ItemProperty -Path ("Registry::$hklm\$strInstallSource") -name $strNValue04 -ea SilentlyContinue
            $strRetValNew05 = Get-ItemProperty -Path ("Registry::$hklm\$strInstallSource") -name $strNValue05 -ea SilentlyContinue
            $strRetValNew06 = Get-ItemProperty -Path ("Registry::$hklm\$strInstallSource") -name $strNValue06 -ea SilentlyContinue
            $strRetValNew07 = Get-ItemProperty -Path ("Registry::$hklm\$strInstallSource") -name $strNValue07 -ea SilentlyContinue
            $strRetValNew10 = Get-ItemProperty -Path ("Registry::$hklm\$strInstallSource") -name $strNValue10 -ea SilentlyContinue
            $strRetValNew12 = Get-ItemProperty -Path ("Registry::$hklm\$strInstallSource") -name $strNValue12 -ea SilentlyContinue
            #$strRetValNew13 = Get-ItemProperty -Path ("Registry::$hklm\$strInstallSource") -name $strNValue13 -ea SilentlyContinue
            $strRetValNew11 = Get-ItemProperty -Path ("Registry::$hklm\$strInstallSource2") -name $strNValue11 -ea SilentlyContinue
            $strProdCode = $strRetValNew07
            if ($strProdCode) {    $strRetValNew08 = (($strProdCode.UninstallString).substring(14)) } else {$strRetValNew08 =""}
            $pregkey="Registry::$hklm\$strInstallSource2"
            $arrSubKeys2 = Get-ChildItem -Path $pregKey -ea SilentlyContinue
            $strRetValue10 = $strNewRTMSource +$strRetValue09.MediaPackage
            if (test-path($strRetValNew02 + "\" + $strRetValue01)) {$OrgFileExist="Y"} else {$OrgFileExist="N"}
            if (test-path($strRetValNew04.LocalPackage)) {$icachefileexist="Y"} else {$icachefileexist="N"}
            $revid= convert-to-revid -CompressedGuid ($strNValue08.packagecode)
            if (test-path ($strRetValNew04.LocalPackage)) {
                $exrevo=Get-FileRevNumber $strRetValNew04.LocalPackage
                if (!($revid -ieq $exrevo )) {$icachefileexist='Err'}
            }
            ""|Select @{Name="Name"; Expression = {$strRetValue00.productname}},@{Name="Id"; Expression = {$strNValue08.packagecode}},@{Name="RevId"; Expression = {$Revid}},@{Name="Package"; Expression = {$strRetValue01.PackageName}},@{Name="CacheFileName"; Expression = {$strRetValNew04.LocalPackage}},@{Name="CacheIntact"; Expression = {$icachefileexist}},@{Name="Version"; Expression = {$strRetValNew05.DisplayVersion}},@{Name="InstDate"; Expression = {$strRetValNew06.InstallDate}},@{Name="LastUsedSource"; Expression = {$strRetValNew02}},@{Name="OrgFileExist"; Expression = {$OrgFileExist}},@{Name="OrgPath"; Expression = {$strRetValNew02  + $strRetValue01.PackageName}} |export-csv -append -Path $script:outputflnm -NoTypeInformation
            foreach ($strSubKey2 in $arrSubKeys2) {
                $pathid=($strSubKey2.name).replace("$hklm\$strInstallSource2","")
                $revid= convert-to-revid -CompressedGuid $pathid

                $strKeyPath04 = "Installer\Patches\"+$pathid+"\SourceList"
                $strRetValue16= Get-ItemProperty -Path  ("Registry::"+$strSubKey2.name+"\")  -name $strNValue16 -ea SilentlyContinue
                $strRetValue15a= Get-ItemProperty -Path ("Registry::$hkcr\$strKeyPath04")   -name $strNValue15 -ea SilentlyContinue
                $strRetValue14a=Get-ItemProperty -Path  ("Registry::$hkcr\$strKeyPath04") -name $strNValue14 -ea SilentlyContinue
                $strRetValue15b= Get-ItemProperty -Path  ("Registry::$strKeyPath02") -name $strNValue15 -ea SilentlyContinue
                $strRetValue14b= Get-ItemProperty -Path  ("Registry::$strKeyPath02") -name $strNValue14 -ea SilentlyContinue
                $strRetValue17= Get-ItemProperty -Path  ("Registry::$hklm\$strInstallSource2\$pathid\")  -name $strNValue17 -ea SilentlyContinue
                $strRetValue18= Get-ItemProperty -Path  ("Registry::$hklm\$strInstallSource2\$pathid\")  -name $strNValue18 -ea SilentlyContinue
                $strRetValue13a=Get-ItemProperty -Path  ("Registry::$hklm\$strInstallSource2\$pathid\")  -name $strNValue13 -ea SilentlyContinue
                $strRetValue04a= Get-ItemProperty -Path  ("Registry::$hklm\$strInstallSource3\$pathid\")  -name $strNValue04 -ea SilentlyContinue 
                if (test-path($strRetValNew04.LocalPackage)) {$icachefileexist="Y"} else {$icachefileexist="N"}
                if (test-path($strRetValue04a.LocalPackage)) {$PatchCacheExist="Y"} else {$PatchCacheExist="N"}               
                $version=($strRetValue13a.MoreInfoURL).replace("https://support.microsoft.com/?","")
            if (test-path ($strRetValue04a.LocalPackage)) {
                $exrevo=Get-FileRevNumber $strRetValue04a.LocalPackage
                if (!($revid -ieq $exrevo )) {$PatchCacheExist='Err'}
            }

                ""|Select @{Name="Name"; Expression = {$strRetValue17.DisplayName }},@{Name="Id"; Expression = {$pathid}},@{Name="RevId"; Expression = {$Revid}},@{Name="Package"; Expression = {$strRetValue14a.PackageName}},@{Name="CacheFileName"; Expression = {$strRetValue04a.LocalPackage}},@{Name="CacheIntact"; Expression = {$PatchCacheExist}},@{Name="Version"; Expression = {"$version"}},@{Name="InstDate"; Expression = {$strRetValue18.Installed}},@{Name="LastUsedSource"; Expression = {($strRetValue15a.LastUsedSource).substring(4)}},@{Name="OrgFileExist"; Expression = {$icachefileexist}},@{Name="OrgPath"; Expression = {$strRetValue13a.MoreInfoURL}} |export-csv -append -Path $script:outputflnm -NoTypeInformation
            }
        }#####
#        else {
 #       write-host $strRetValue00.productname
  #      }
    }
    $report=import-csv -Path $script:outputflnm

    $missingfiles=$report| select Name, Package, CacheFileName, CacheIntact, Version, OrgFileExist, OrgPath
#################
$global:autopopulated=$false
[xml] $xaml = @"
<Window 
        xmlns="http://schemas.microsoft.com/winfx/2006/xaml/presentation"
        xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml"
        Title="MissedFiles" BorderThickness="2" FontFamily="Georgia" FontSize="10" Foreground="Maroon"  ResizeMode="NoResize" Background="White" WindowStartupLocation = "CenterScreen" WindowStyle="None" AllowsTransparency="False" Height="400" Width="800">
    <Grid>
         <Grid.RowDefinitions>
                <RowDefinition Height="30" />
                <RowDefinition Height="*" />
        </Grid.RowDefinitions>
        <Grid Grid.Row="0">
            <Grid.ColumnDefinitions>                        
                <ColumnDefinition Width="*" />
                <ColumnDefinition Width="125" />
                <ColumnDefinition Width="125" />
                <ColumnDefinition Width="125" />
            </Grid.ColumnDefinitions>                        
            <Label Grid.Row="0"  Grid.Column="0" Content="Installer Cache status report"  Background="#FFF9EDED" Foreground="#FF400101" HorizontalAlignment="Left" Height="30" VerticalAlignment="Top" FontWeight="Bold" FontStyle="Italic" FontSize="11" Width="650" VerticalContentAlignment="Center" />
            <Button Name="btn_save" Height="30" Grid.Row="2" Width="125"  Background="AntiqueWhite" Grid.Column="1">Save </Button>
            <Button Name="btn_FilterMissing" Height="30" Grid.Row="2" Width="125"  Background="AntiqueWhite" Grid.Column="2">Filter Missing only</Button>
            <Button Name="btn_xit" Height="30" Grid.Row="2" Width="125"  Background="AntiqueWhite" Grid.Column="3">Exit </Button>
        
        </Grid>
        <DataGrid Grid.Row="1"
            AutoGenerateColumns="False" 
            HorizontalAlignment="Left" 
            Name="MissedFiles" 
            AlternatingRowBackground="Azure"
            VerticalAlignment="Top" 
            Width="800"
            Height="370"
            HeadersVisibility="Column" 
            CanUserSortColumns="True" IsSynchronizedWithCurrentItem="true">
            <DataGrid.Columns>
                <DataGridTextColumn Header="Name"
                                Binding="{Binding Name}"
                                Width="Auto"   IsReadOnly="True" 
                                />
                <DataGridTextColumn Header="Package"
                                Binding="{Binding Package}" 
                                Width="Auto"  IsReadOnly="True"
                                />
                <DataGridTextColumn Header="CacheFileName"
                                Binding="{Binding CacheFileName}" 
                                Width="Auto"  IsReadOnly="True"
                                />
                <DataGridTextColumn Header="CacheIntact"
                                Binding="{Binding CacheIntact}" 
                                Width="Auto"  IsReadOnly="True"
                                />
                <DataGridTextColumn Header="Version"
                                Binding="{Binding Version}" 
                                Width="Auto"  IsReadOnly="True"
                                />
                <DataGridTextColumn Header="OrgFileExist"
                                Binding="{Binding OrgFileExist}" 
                                Width="Auto"  IsReadOnly="True"
                                />
                <DataGridTextColumn Header="OrgPath"
                                Binding="{Binding OrgPath}" 
                                Width="Auto"  IsReadOnly="True"
                                />
            </DataGrid.Columns>
        </DataGrid >
    </Grid>
</Window>
"@
$reader=(New-Object System.Xml.XmlNodeReader $xaml)
$Frmcachefiles=[Windows.Markup.XamlReader]::Load( $reader )
$dg = $Frmcachefiles.FindName("MissedFiles")
$btn_close = $Frmcachefiles.FindName("btn_xit")
