<ColumnDefinition Width="170" />
<ColumnDefinition Width="180" />
<ColumnDefinition Width="80" />
</Grid.ColumnDefinitions>
<ComboBox Name="cmbcacheflnm" Grid.Row="0" Grid.Column="0" Height="30" Width="170" Margin="0,0,0,0" Background="#FFF9EDED" Foreground="#FF400101" HorizontalAlignment="Left" />       
<TextBox Grid.Row="0"  Grid.Column="1" x:Name="txtsrcflnm"  Background="#FFF9EDED" Foreground="#FF400101" HorizontalAlignment="Left" Height="30" Width="180"  TextWrapping="Wrap" Text="" VerticalAlignment="Top"  VerticalContentAlignment="Center" />
<Button Grid.Row="0" Grid.Column="2" x:Name="btn_brw" Content="..." HorizontalAlignment="Center" Height="30" VerticalAlignment="Center" Width = "80" Margin ="0,0,0,0"/>
</Grid>
