<Grid.ColumnDefinitions>
<ColumnDefinition Width="220" />
<ColumnDefinition Width="220" />
</Grid.ColumnDefinitions>
<Button Grid.Row="0" Grid.Column="0" x:Name="btn_Fix" Content="Proceed" HorizontalAlignment="Center" Height="30" VerticalAlignment="Center" Width = "440" Margin ="0,0,0,0"/>
<Button Grid.Row="0" Grid.Column="1" x:Name="btn_Cancle" Content="Cancle" HorizontalAlignment="Center" Height="30" VerticalAlignment="Center" Width = "440" Margin ="0,0,0,0"/>
</Grid>
</Grid>
</Window>
'
[xml]$xaml = @"
$xmldrvar
"@ 
$sqllists=$null
$reader=(New-Object System.Xml.XmlNodeReader $xaml)
$orgsrcForm=[Windows.Markup.XamlReader]::Load( $reader )
$btn_brw = $orgsrcForm.FindName("btn_brw")
$btn_Fix = $orgsrcForm.FindName("btn_Fix")
$btn_Cancle= $orgsrcForm.FindName("btn_Cancle")
$txtsrcflnm= $orgsrcForm.FindName("txtsrcflnm")
$cmbcacheflnm= $orgsrcForm.FindName("cmbcacheflnm")
$cmbcacheflnm.ItemsSource = $missingfiles 
$cmbcacheflnm.DisplayMemberPath = 'CacheFileName'
$cmbcacheflnm.SelectedIndex = 0
$btn_brw.Add_Click({    
       $cacheflnm=$cmbcacheflnm.text
       $pkglist=$missingfiles| select Package,CacheFileName| where {($_.CacheFileName -ieq $cacheflnm)}
       foreach($pkg in $pkglist) {   
            $pkgnm=$pkg.package
            $srcfile=[System.String] (Get-FileName -flnmfilter $pkgnm -initialDirectory $script:curpath)
            if (!([String]::IsNullOrEmpty($srcfile))) {
                $txtsrcflnm.text= $srcfile
            }
       }
})
$btn_Fix.add_click({
    Show-Pleasewait -path $script:curpath
    $cacheflnm=$cmbcacheflnm.text
    $pkglist=$missingfiles| select Package,revid, CacheFileName| where {($_.CacheFileName -ieq $cacheflnm)}
    foreach($pkg in $pkglist) {   
        $pkgnm=$pkg.package
        $revid=$pkg.revid            
        $srcfile=$txtsrcflnm.text
        $newrevno=Get-FileRevNumber -File $srcfile
        if ($newrevno -ieq $revid) {
            if (!(test-path ($pkg.cachefilename))) {
                copy-item $srcfile -Destination $pkg.cachefilename
            }
        }
    }
    if ($Script:SyncHash.Form) {        $Script:SyncHash.Form.Close()}
    [System.GC]::Collect()
})
$btn_Cancle.Add_Click({     
    $orgsrcForm.close()
})
$orgsrcForm.ShowDialog() 

}
