    xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml"
    Title="MSIMSPFixerWindow" Topmost="False" Height="330" Width="440"  BorderThickness="8" BorderBrush="Maroon" Background="White" WindowStartupLocation = "CenterScreen" WindowStyle="None" AllowsTransparency="True" >    
    <Grid>
     <Grid.RowDefinitions>
                <RowDefinition Height="30" />
                <RowDefinition Height="*" />
                <RowDefinition Height="30" />
                <RowDefinition Height="30" />
