        <Image Grid.Row="1" Height="208" Width = "272" Name="testimg" Source="'+$scr1imgfile+'" Stretch="Fill" Margin = "0,0,0,0"   />    	        
<Grid Grid.Row="2">
<Grid.ColumnDefinitions>
<ColumnDefinition Width="170" />
<ColumnDefinition Width="220" />
