</Grid.ColumnDefinitions>
<ComboBox Name="Combover" Grid.Row="0" Grid.Column="0" Height="30" Width="170" Margin="0,0,0,0" Background="#FFF9EDED" Foreground="#FF400101" HorizontalAlignment="Left" />       
<TextBox Grid.Row="0"  Grid.Column="1" x:Name="txtver"  Background="#FFF9EDED" Foreground="#FF400101" HorizontalAlignment="Left" Height="30" Width="220"  TextWrapping="Wrap" Text="" VerticalAlignment="Top"  VerticalContentAlignment="Center" />
<Button Grid.Row="0" Grid.Column="2" x:Name="btn_brw" Content="..." HorizontalAlignment="Center" Height="30" VerticalAlignment="Center" Width = "35" Margin ="0,0,0,0"/>
