</Grid>
</Grid>
</Window>
'
[xml]$xaml = @"
$xmldrvar
"@ 
$sqllists=$null
$reader=(New-Object System.Xml.XmlNodeReader $xaml)
$orgsrcForm=[Windows.Markup.XamlReader]::Load( $reader )
$btn_brw = $orgsrcForm.FindName("btn_brw")
$btn_Fix = $orgsrcForm.FindName("btn_Fix")
$btn_Cancle= $orgsrcForm.FindName("btn_Cancle")
$txt_txtver= $orgsrcForm.FindName("txtver")
$cmb_Combover= $orgsrcForm.FindName("Combover")
$cmb_Combover.ItemsSource = $missingfiles | select version,cacheintact -Unique | Sort-Object -Property version |where {($_.cacheintact -ieq "N")}
$cmb_Combover.DisplayMemberPath = 'version'
$cmb_Combover.SelectedIndex = 0
$btn_brw.Add_Click({     
       $srcfolder=[System.String] (StartupFolderSelector)
       if (!([String]::IsNullOrEmpty($srcfolder))) {
        $txt_txtver.text= $srcfolder
       }
})
$btn_Fix.add_click({
$orgsrcForm.isenabled=$false
$fixctr=0
Show-Pleasewait -path $script:curpath
sleep -Seconds 2
$srcfolder=$txt_txtver.text
$ver=$cmb_Combover.text
if (!([String]::IsNullOrEmpty($srcfolder) -and [String]::IsNullOrEmpty($ver ))) {
$xmissingfiles=$missingfiles| select Name,RevId,Package,CacheFileName,LastUsedSource,version| where {($_.version -ieq $ver)}
foreach ($files in $xmissingfiles) {
##write-host $files.cachefilename
    if (!(test-path ($files.cachefilename))) { 
        $lastlocation=$files.LastUsedSource+$files.package
        $lastlocpcss=$lastlocation.split("\")
        $pctr=$lastlocpcss.length
        $ctr=1
        $loc=0
        while ($ctr -lt $pctr) {
            $loc=$Loc+$lastlocpcss[($ctr -1)].length
            $chkpath=$srcfolder+$lastlocation.substring($loc)
            if (test-path $chkpath) {
                $newrevno=Get-FileRevNumber -File $chkpath
                if ($newrevno -ieq $files.revid ) {
                    if (!(test-path -$files.cachefilename)) {
                        copy-item $chkpath -Destination $files.cachefilename
                    }
                    $fixctr ++
               }             
            }
            $loc++
            $ctr++
        } 

    }
$txt_txtver.text=""
$orgsrcForm.isenabled=$true
#write-host "exit"
}
if ($Script:SyncHash.Form) {$Script:SyncHash.Form.Close()}
PSMsgbox -Message "$fixctr missing files are restored from path $srcfolder." -Title MSIMSPFixer -Buttons OK -icon Information

#[System.GC]::Collect()
} 
})
 

$btn_Cancle.Add_Click({     
    $orgsrcForm.close()
})
$orgsrcForm.ShowDialog() 

}
Function SrcServer-processor {
param ([array]$serverlist )
    $missingfiles=import-csv -path $script:outputflnm |select Name,RevId, CacheFileName,cacheintact | where {$_.cacheintact -ieq 'N'}
    $totalmisingfiles=$missingfiles.count
    foreach ($server in $serverlist) {
        Show-Pleasewait -path $script:curpath
        $fixctr=0
        $srcpath="\\$server\c$\windows\installer\*"
        $msipmspfiles=Get-ChildItem $srcpath -include "*.msi","*.msp"
        foreach($files in $msipmspfiles) {
            $revno=Get-FileRevNumber -File $files
            if ($revno) {
                $ktr=0
                $missing=$missingfiles|select Name,RevId, CacheFileName,cacheintact| where {$_.cacheintact -ieq 'N'}
                $missingfiles=$missing
                foreach($orgfile in $missingfiles) {
                    if ($orgfile.revid -ieq $revno) {
                        $targetfile=$orgfile.cachefilename
                        if (!(test-path ($targetfile))) {
                            Copy-Item $files -Destination $targetfile
                        }
                        $missingfiles[$ktr].cacheintact="Y"
                        $fixctr ++
                    }
                    $ktr++
                }
            }    
        }
        if ($Script:SyncHash.Form) {        $Script:SyncHash.Form.Close()}
        [System.GC]::Collect()
        PSMsgbox -Message "$fixctr missing files are restored from server $server." -Title MSIMSPFixer -Buttons OK -icon Information
    }

}
Function SrcFolder-processor {
param ([string]$srcpath)
if ($srcpath.Substring(($srcpath.Length-1),1) -ieq '\') {$srcpath=$srcpath+"*"} else {$srcpath=$srcpath+"\*"}
$msipmspfiles=Get-ChildItem $srcpath -include "*.msi","*.msp"
$missingfiles=import-csv -path $script:outputflnm |select Name,RevId, CacheFileName,cacheintact | where {$_.cacheintact -ieq 'N'}
$tootalmisingfiles=$missingfiles.count
$fixctr=0
foreach($files in $msipmspfiles) {
    $revno=Get-FileRevNumber -File ($files.fullname)
    if ($revno) {
            $found=$missingfiles|select CacheFileName, revid | where {$_.revid -ieq $revno}
            if ($found) {
                $targetfile=$found.cachefilename
                if (!(test-path ($targetfile))) {
                    Copy-Item $files -Destination $targetfile
                }
                $fixctr ++
                #write-host $targetfile " restored"
            } 
    }    
}
PSMsgbox -Message "$fixctr out of $tootalmisingfiles missing files are restored." -Title MSIMSPFixer -Buttons OK -icon Information
}
