function ConvertFrom-Base64 { 
    param
    (        
        [string] $TargetFilePath,
        [string] $imgFilePath
    )
    $imgFilePath | out-file "$env:temp\tmp.txt"
    $SourceFilePath= "$env:temp\tmp.txt"
    $SourceFilePath = Resolve-PathSafe $SourceFilePath
    $TargetFilePath = Resolve-PathSafe $TargetFilePath
    $bufferSize = 9000 # should be a multiplier of 4
    $buffer = New-Object char[] $bufferSize
    $reader = [System.IO.File]::OpenText($SourceFilePath)
    $writer = [System.IO.File]::OpenWrite($TargetFilePath)
    $bytesRead = 0
    do
    {
        $bytesRead = $reader.Read($buffer, 0, $bufferSize);
        $bytes = [Convert]::FromBase64CharArray($buffer, 0, $bytesRead);
        $writer.Write($bytes, 0, $bytes.Length);
    } while ($bytesRead -eq $bufferSize);
    $reader.Dispose()
    $writer.Dispose()
}
Function Get-FileRevNumber    {
[CmdletBinding()]
Param (
[Parameter(Mandatory=$True, Position=0)][Alias("F")][String]$File
)        
$WindowsInstaller = $null;
$SummaryInfo = $null;
Try
    {
    $WindowsInstaller = New-Object -ComObject "WindowsInstaller.Installer";
    If(!($WindowsInstaller)) {Return $null}
    [Object[]]$args = @($File, 0);
    $SummaryInfo = $WindowsInstaller.GetType().InvokeMember("SummaryInformation", [System.Reflection.BindingFlags]::GetProperty, $null, $WindowsInstaller, $args);
    If(!($SummaryInfo)) {Return $null}
    [Object[]]$args = @(9);
    $rn = $SummaryInfo.GetType().InvokeMember("Property", [System.Reflection.BindingFlags]::GetProperty, $null, $SummaryInfo, $args);
    If(!($rn)) {Return $null}
    Return $rn.ToString();
    }
Catch
    {
    $methods = $_.Exception.Data.Values.MethodName;
    $codeLine = $_.InvocationInfo.Line;
    $positionMessage = $_.InvocationInfo.PositionMessage;
    $ErrorMessage = $_.Exception.Message;
    $nullInstaller = "Windows Installer is null? " + ($WindowsInstaller -eq $null);
    $nullSumInfo = "SummaryInfo is null? " + ($SummaryInfo -eq $null);
    Write-Host -ForegroundColor Red "Failed to access the metadata of the file ""$File""!";
    Return $null
    }
Finally
    {
        Write-Verbose "Exits ""Get-FileRevisionNumber""!";
    }
}
Function convert-to-revid    {
         [CmdletBinding()]
         [OutputType([System.String])]
         Param 
         (
             [Parameter(Mandatory=$True)][ValidatePattern('^[0-9a-fA-F]{32}$')][string]$CompressedGuid
         )

         $Indexes=New-Object System.Collections.Specialized.OrderedDictionary;
         $Indexes.Add(0,8); 
         $Indexes.Add(8,4); 
         $Indexes.Add(12,4); 
         $Indexes.Add(16, 2); 
         $Indexes.Add(18,2); 
         $Indexes.Add(20,2); 
         $Indexes.Add(22,2); 
         $Indexes.Add(24,2); 
         $Indexes.Add(26,2); 
         $Indexes.Add(28,2); 
         $Indexes.Add(30,2); 

         $Guid = '{';
         foreach ($index in $Indexes.GetEnumerator()) 
         {
            $part = $CompressedGuid.Substring($index.Key, $index.Value).ToCharArray();
            [Array]::Reverse($part);
            $Guid += $part -Join '';
         }
         
         $Guid = $Guid.Insert(9,'-').Insert(14, '-').Insert(19, '-').Insert(24, '-');
         $Guid += '}';

         Return $Guid;
    }
